import { auth } from "../../firebase";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBTextArea,
  MDBFile,
} from "mdb-react-ui-kit";
import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { MDBCalendar } from 'mdb-react-calendar';
function Leave() {
  const [errorMsg, setErrorMsg] = useState("");
  // if (!value.name || !value.EmpID || !value.FromDate || !value.ToDate || value.Reason) {
  //   setErrorMsg("Fill all fields");
  //   return;
  // }
  let history = useHistory();
  const [userData, setUserData] = useState({
    name: "",
    EmpID: "",
    FromDate: "",
    ToDate: "",
    Reason: "",
    imag: "",
  });

  let name, value;
  const postUserData = (event) => {
    name = event.target.name;
    value = event.target.value;

    setUserData({ ...userData, [name]: value });
  };
  const submitData = async (event) => {

      
   
   
    event.preventDefault();
    const { name, EmpID, FromDate, ToDate, Reason, imag } = userData;

    
    const res = fetch("https://qudient-employee-default-rtdb.firebaseio.com/EmpLeaveDataRecords.json",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name,
          EmpID,
          FromDate,
          ToDate,
          Reason,
          imag,
        }),
      }
    );

    if (res) {
      alert("Data Successfully Submit ");
    } else {
      alert("Please enter data ");
    }
    history.push("/card");
    window.location.reload(false);
  


    const today = new Date();

    const getStringDate = (date: Date) => date.toLocaleDateString().replaceAll('.', '/');
  
    const addDays = (date: Date, days: number) => {
      return new Date(date.getFullYear(), date.getMonth(), date.getDate() + days);
    };
  
    const myEvents = [
      {
        summary: 'JS Conference',
        description: '',
        start: {
          date: getStringDate(today),
        },
        end: {
          date: getStringDate(today),
        },
        color: {
          background: 'rgb(207, 224, 252)',
          foreground: 'rgb(10, 71, 169)',
        },
      },
      {
        summary: 'Vue Meetup',
        description: '',
        start: {
          date: getStringDate(addDays(today, 1)),
        },
        end: {
          date: getStringDate(addDays(today, 5)),
        },
        color: {
          background: 'rgb(235, 205, 254)',
          foreground: 'rgb(110, 2, 177)',
        },
      },
      {
        summary: 'Angular Meetup',
        description:
          'Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur',
        start: {
          date: getStringDate(addDays(today, -3)),
          time: '10:00',
        },
        end: {
          date: getStringDate(addDays(today, 3)),
          time: '14:00',
        },
        color: {
          background: 'rgb(199, 245, 217)',
          foreground: 'rgb(11, 65, 33)',
        },
      },
      {
        summary: 'React Meetup',
        description: '',
        start: {
          date: getStringDate(addDays(today, 5)),
        },
        end: {
          date: getStringDate(addDays(today, 8)),
        },
        color: {
          background: 'rgb(253, 216, 222)',
          foreground: 'rgb(121, 6, 25)',
        },
      },
      {
        summary: 'Meeting',
        description: '',
        start: {
          date: getStringDate(addDays(today, 1)),
          time: '8:00',
        },
        end: {
          date: getStringDate(addDays(today, 1)),
          time: '12:00',
        },
        color: {
          background: 'rgb(207, 224, 252)',
          foreground: 'black',
        },
      },
      {
        summary: 'Call',
        description: '',
        start: {
          date: getStringDate(addDays(today, 2)),
          time: '11:00',
        },
        end: {
          date: getStringDate(addDays(today, 2)),
          time: '14:00',
        },
        color: {
          background: 'black',
          foreground: 'black',
        },
      },
    ];
  };



  return (
    <MDBContainer fluid style={{ height: 1200, width: 1200 }}>
      <MDBRow className="d-flex justify-content-center align-items-center">
        <MDBCol lg="9" className="my-5">
          <h1 className="text-white mb-4">Apply for Leave </h1>
          <form method="POST">
            <MDBCard>
              <MDBCardBody className="px-4">
                <MDBRow ClassName="col-md-6">
                  <MDBCol md="6" className="ps-5">
                    Name
                    <MDBInput
                      label="Name"
                      size="lg"
                      id="form2"
                      type="text"
                      name="name"
                      required
                      value={userData.name}
                      onChange={postUserData}
                    />
                  </MDBCol>
                  <MDBCol md="6" className="ps-5">
                    Emp ID
                    <MDBInput
                      label="Emp ID"
                      size="lg"
                      id="form2"
                      type="text"
                      name="EmpID"
                      required
                      value={userData.EmpID}
                      onChange={postUserData}
                    />
                  </MDBCol>
                </MDBRow>

                <hr className="mx-n10" />
                <MDBRow ClassName="col-md-6">
                  <MDBCol md="6" className="ps-5">
                    <MDBInput
                      label="From Date"
                      size="lg"
                      id="form2"
                      type="date"
                      name="FromDate"
                      required
                      value={userData.FromDate}
                      onChange={postUserData}
                    />
                  </MDBCol>
                  <MDBCol md="6" className="ps-5">
                    <MDBInput
                      label="To Date"
                      size="lg"
                      id="form2"
                      type="date"
                      name="ToDate"
                      required
                      value={userData.ToDate}
                      onChange={postUserData}
                    />
                  </MDBCol>
                </MDBRow>

                <hr className="sm-n3" />

                <MDBRow className="align-items-center pt-4 pb-3">
                  <MDBCol md="3" className="ps-5">
                    <h6 className="mb-0">Reason</h6>
                  </MDBCol>

                  <MDBCol md="9" className="pe-5">
                    <MDBTextArea
                      label="Reason"
                      id="textAreaExample"
                      rows={2}
                      name="Reason"
                      value={userData.Reason}
                      onChange={postUserData}
                    />
                  </MDBCol>
                </MDBRow>

                <hr className="mx-n3" />

                <MDBRow className="align-items-center pt-4 pb-3">
                  <MDBCol md="3" className="ps-5">
                    <h6 className="mb-0">Medical certificate </h6>
                  </MDBCol>

                  <MDBCol md="9" className="pe-5">
                    <MDBFile
                      size="lg"
                      id="customFile"
                      name="imag"
                      value={userData.imag}
                      onChange={postUserData}
                    />
                    <div className="small text-muted mt-2">
                      Upload your authenticate doc, Max file size 50 MB
                    </div>
                  </MDBCol>
                  
                </MDBRow>
                < b className='text-danger' >{errorMsg}</b>
                <hr className="mx-n3" />
                
               
                <MDBBtn
                  color="success"
                  className="mb-4"
                  size="lg"
                  onClick={submitData}
                >
                  Submit
                </MDBBtn>
              </MDBCardBody>
            </MDBCard>
          </form>
        </MDBCol>
      </MDBRow>
      <MDBCalendar defaultEvents={myEvents} />
    </MDBContainer>
    
  );
}

export default Leave;
