import { auth } from "../../firebase";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBTextArea,
  MDBFile,
} from "mdb-react-ui-kit";
import React, { useState } from "react";

function Leave() {
  const [userData, setUserData] = useState({
    name: "",
    EmpID: "",
    FromDate: "",
    ToDate: "",
    Reason: "",
    imag: "",
  });

  let name, value;
  const postUserData = (event) => {
    name = event.target.name;
    value = event.target.value;

    setUserData({ ...userData, [name]: value });
  };
  const submitData = async (event) => {
    event.preventDefault();
    const { name, EmpID, FromDate, ToDate, Reason, imag } = userData;

    const res = fetch(
      "https://qudient-employee-default-rtdb.firebaseio.com/EmpDataRecords.json",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name,
          EmpID,
          FromDate,
          ToDate,
          Reason,
          imag,
        }),
      }
    );
    if (res) {
      alert("Data Record");
    } else {
      alert("Please enter data ");
    }
  };

  return (
    <MDBContainer fluid style={{ height: 1200, width: 1200 }}>
      <MDBRow className="d-flex justify-content-center align-items-center">
        <MDBCol lg="9" className="my-5">
          <h1 className="text-white mb-4">Apply for Leave </h1>

          <MDBCard>
            <MDBCardBody className="px-4">
              <MDBRow ClassName="col-md-6">
                <MDBCol md="6" className="ps-5">
                  Name
                  <MDBInput
                    label="Name"
                    size="lg"
                    id="form2"
                    type="text"
                    name="name"
                    value={userData.name}
                    onChange={postUserData}
                  />
                </MDBCol>
                <MDBCol md="6" className="ps-5">
                  Emp ID
                  <MDBInput
                    label="Emp ID"
                    size="lg"
                    id="form2"
                    type="text"
                    name="EmpID"
                    value={userData.EmpID}
                    onChange={postUserData}
                  />
                </MDBCol>
              </MDBRow>
              <hr className="mx-n10" />
              <MDBRow ClassName="col-md-6">
                <MDBCol md="6" className="ps-5">
                  <MDBInput
                    label="From Date"
                    size="lg"
                    id="form2"
                    type="date"
                    name="FromDate"
                    value={userData.FromDate}
                    onChange={postUserData}
                  />
                </MDBCol>
                <MDBCol md="6" className="ps-5">
                  <MDBInput
                    label="To Date"
                    size="lg"
                    id="form2"
                    type="date"
                    name="ToDate"
                    value={userData.ToDate}
                    onChange={postUserData}
                  />
                </MDBCol>
              </MDBRow>
              <hr className="sm-n3" />
              <MDBRow className="align-items-center pt-4 pb-3">
                <MDBCol md="3" className="ps-5">
                  <h6 className="mb-0">Reason</h6>
                </MDBCol>

                <MDBCol md="9" className="pe-5">
                  <MDBTextArea
                    label="Reason"
                    id="textAreaExample"
                    rows={2}
                    name="Reason"
                    value={userData.Reason}
                    onChange={postUserData}
                  />
                </MDBCol>
              </MDBRow>
              <hr className="mx-n3" />
              <MDBRow className="align-items-center pt-4 pb-3">
                <MDBCol md="3" className="ps-5">
                  <h6 className="mb-0">Medical certificate </h6>
                </MDBCol>

                <MDBCol md="9" className="pe-5">
                  <MDBFile
                    size="lg"
                    id="customFile"
                    name="imag"
                    value={userData.imag}
                    onChange={postUserData}
                  />
                  <div className="small text-muted mt-2">
                    Upload your authenticate doc, Max file size 50 MB
                  </div>
                </MDBCol>
              </MDBRow>
              <hr className="mx-n3" />
              <MDBBtn
                className="mb-4"
                size="lg"
                type="submit"
                onClick={handleJustifyClick}
              >
                {" "}
                Submit
              </MDBBtn>{" "}
              or
              <MDBBtn href="/login" className="mb-4" size="lg " type="submit">
                {" "}
                Logout
              </MDBBtn>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default Leave;
